--
-- Name: stream_info; Type: VIEW; Schema: system_views; Owner: potadmindb
--

CREATE VIEW system_views.stream_info AS
 SELECT split_part((es.stream_id)::text, '|'::text, 1) AS category,
    split_part((es.stream_id)::text, '|'::text, 2) AS id,
    min((es.created_at)::text) AS created_at,
    max((es.created_at)::text) AS updated_at,
    count(es.stream_id) AS event_count,
    count(pa.*) AS pending_acknowledgement_count
   FROM (event_store.event_stream es
     LEFT JOIN event_store.pending_acknowledgement pa ON ((((pa.stream_id)::text = (es.stream_id)::text) AND ((pa.created_at)::text = (es.created_at)::text) AND (pa.version = es.version))))
  GROUP BY (split_part((es.stream_id)::text, '|'::text, 1)), (split_part((es.stream_id)::text, '|'::text, 2))
  ORDER BY (max((es.created_at)::text)) DESC;


